pages/blog-post:
---
blog:
    recent_posts:
        limit: {{theme_settings.blog_posts_per_page}}
products:
    new:
        limit: {{theme_settings.homepage_new_products_count}}
    featured:
        limit: {{theme_settings.homepage_featured_products_count}}
    top_sellers:
        limit: {{theme_settings.homepage_top_products_count}}
---
{{#partial "page"}}

    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#unless theme_settings.hide_blog_page_heading }}
        <h1 class="page-heading">{{ blog.name }}</h1>
    {{/unless}}

    <div class="page blog-page">
        {{#if theme_settings.show_sidebar}}
            <aside class="page-sidebar{{#if theme_settings.show_sidebar}} sidebar-show{{else}} sidebar-hide{{/if}}">
                {{> components/halothemes/blogs/halo-blog-sidebar post=blog.recent_posts limit_post=theme_settings.blog_posts_per_page}}
            </aside>
        {{/if}}

        <main class="page-content" id="blog-listing-container">
            {{#if theme_settings.show_sidebar}}
                <div class="sidebar-label{{#if theme_settings.show_sidebar}} show-sidebar{{/if}}">
                    <span class="sidebar_more">{{lang 'common.show_sidebar'}}</span>
                    <span class="icon-filter">
                        <svg class="icon"><use xlink:href="#icon-filter"></use></svg>
                    </span>
                </div>
            {{/if}}

            {{> components/blog/post post=blog.post show_tags=true}}
        </main>
    </div>
{{/partial}}
{{> layout/base}}

components/common/footer:
{{#if banners.bottom}}
    <div class="banners" data-banner-location="bottom">
        {{#each (limit banners.bottom_metadata 1)}}
            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                <div data-event-type="promotion-click">
                    {{{this.content}}}
                </div>
            </div>
        {{/each}}
    </div>
{{/if}}

{{#if theme_settings.halo_footer_layout '==' 'footer-layout-1'}}
    {{> components/halothemes/footer/footer-layout-1}}
{{else}}
    {{> components/halothemes/footer/footer-layout-2}}
{{/if}}

{{#if theme_settings.halo_recently_viewed_products '==' false}}
    {{#if theme_settings.enable_back_to_top}}
        <div id="back-to-top">
            <a href="javascript:void(0)">Back To Top<svg class="icon"><use xlink:href="#icon-keyboard-arrow-down"></use></svg></a>
        </div>
    {{/if}}
{{/if}}

components/halothemes/halo-megamenu:
