components/halothemes/halo-before-you-le:
{{#if theme_settings.halo_before_you_leave}}
	<section id="before-you-leave" class="before-you-leave side_popup" data-time="{{theme_settings.before-you-leave-time}}">
        <div class="before-you-leave-inner scrollbar">
			{{#if theme_settings.before-you-leave-img '!=' ''}}
				<div class="before-you-leave-banner">
					<div class="banner-inner ">
        				<a href="javascript:void(0)" class="halo-close halo-close" aria-label="Close Before You Leave Popup">&times;</a>
						<a class="image-wrapper" href="{{theme_settings.before-you-leave-link}}" title="{{theme_settings.before-you-leave-title}}">
							<img class="lazyload" src="" data-src="https://cdn11.bigcommerce.com/s-{{settings.store_hash}}/product_images/uploaded_images/{{theme_settings.before-you-leave-img}}" alt="{{theme_settings.before-you-leave-title}}">
						</a>
						<div class="content text-center">
							<div class="content-inner">
								{{#if theme_settings.before-you-leave-title '!=' ''}}
									<h2 class="title">{{theme_settings.before-you-leave-title}}</h2>
								{{/if}}
								{{#if theme_settings.before-you-leave-text-1 '!=' ''}}
									<p class="desc1">{{theme_settings.before-you-leave-text-1}}</p>
								{{/if}}
								{{#if theme_settings.before-you-leave-heading-1 '!=' ''}}
									<h3 class="heading1"><span>{{theme_settings.before-you-leave-heading-1}}</span></h3>
								{{/if}}
								{{#if theme_settings.before-you-leave-text-2 '!=' ''}}
									<p class="desc2">{{theme_settings.before-you-leave-text-2}}</p>
								{{/if}}
								{{#if theme_settings.before-you-leave-heading-2 '!=' ''}}
									<h3 class="heading2">{{theme_settings.before-you-leave-heading-2}}</h3>
								{{/if}}
							</div>
							{{#if theme_settings.before-you-leave-btn-text '!=' ''}}
