components/common/social-links:
{{#or social_media theme_settings.social_icon_message_link theme_settings.social_icon_snapchat_link }}
    <ul class="socialLinks socialLinks--alt">
        {{#if social_media}}
            {{#each social_media}}
                <li class="socialLinks-item {{display_name}}">
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener" title="Open {{{capitalize display_name}}} in a new tab">
                        <svg aria-hidden="true"><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                </li>
            {{/each}}
        {{/if}}
        {{#if theme_settings.social_icon_snapchat_link '!=' ''}}
            <li class="socialLinks-item snapchat">
                <a class="icon icon--snapchat" href="{{theme_settings.social_icon_snapchat_link}}" target="_blank" rel="noopener" title="Open Snapchat in a new tab">
                    <svg><use xlink:href="#icon-snapchat-ghost" /></svg>
                </a>
            </li>
        {{/if}}
        {{#if theme_settings.social_icon_message_link '!=' ''}}
            <li class="socialLinks-item messenger">
                <a class="icon icon--messenger" href="{{theme_settings.social_icon_message_link}}" target="_blank" target="_blank" rel="noopener" title="Open Messenger in a new tab">
                    <svg><use xlink:href="#icon-messenger" /></svg>
                </a>
            </li>
        {{/if}}
    </ul>
{{/or}}

